<h1 style="background-color: aquamarine;">Angular Express</h1>
<b>Enter user's data</b><br>
<form #userData="ngForm" (ngSubmit)="addUser(userData)">
  User Id <input type="text" name="userId" ngModel /><br>
  Password <input type="text" name="password" ngModel /><br>
  Email Id <input type="email" name="emailId" ngModel /><br>
  <input type="submit" value="Add User" />&nbsp;&nbsp;
  <input type="button" value="Get User" (click)="getAllUsers()" />
</form>
<br>
<label>{{ expresponse }}</label>
<br><br>
<h2>User Data</h2>

<table border="1">
  <tr>
    <th>User ID</th>
    <th>Password</th>
    <th>Email ID</th>
    <th>Actions</th> <!-- Add a column for actions -->
  </tr>
  <tr *ngFor="let user of userList">
    <td>{{ user.uid }}</td>
    <td>{{ user.password }}</td>
    <td>{{ user.emailId }}</td>
    <td>
      <!-- Add update and delete buttons for each user -->
      <button (click)="updateUser(user)">Update</button>
      <button (click)="deleteUser(user.uid)">Delete</button>
    </td>
  </tr>
</table>
<router-outlet></router-outlet>
