import { Component } from '@angular/core';

interface User {
  id: number;
  userId: string;
  username: string;
  password: string;
}

@Component({
  selector: 'app-root',
  template: `
    <div>
      <h2>User Management</h2>
      <form (ngSubmit)="isEditMode ? updateUser() : addUser()">
        <input [(ngModel)]="newUser.userId" type="text" placeholder="User ID" />
        <input [(ngModel)]="newUser.username" type="text" placeholder="Username" />
        <input [(ngModel)]="newUser.password" type="password" placeholder="Password" />
        <button type="submit">
          {{ isEditMode ? 'Update' : 'Add' }}
        </button>
      </form>
    </div>
    <table>
      <tr>
        <th>User ID</th>
        <th>Username</th>
        <th>Password</th>
        <th>Actions</th>
      </tr>
      <tr *ngFor="let user of users">
        <td>{{ user.userId }}</td>
        <td>{{ user.username }}</td>
        <td>{{ user.password }}</td>
        <td>
          <button (click)="editUser(user)">Edit</button>
          <button (click)="deleteUser(user.id)">Delete</button>
        </td>
      </tr>
    </table>
  `,
})
export class AppComponent {
  users: User[] = [];
  newUser: User = { id: 0, userId: '', username: '', password: '' };
  selectedUser: User | null = null;
  isEditMode = false;

  addUser(): void {
    // Add user logic here
    this.users.push({ ...this.newUser, id: this.users.length + 1 });
    this.newUser = { id: 0, userId: '', username: '', password: '' };
  }

  deleteUser(id: number): void {
    // Delete user logic here
    this.users = this.users.filter((user) => user.id !== id);
  }

  editUser(user: User): void {
    this.isEditMode = true;
    this.selectedUser = { ...user };
  }

  updateUser(): void {
    if (this.selectedUser) {
      const index = this.users.findIndex((user) => user.id === this.selectedUser?.id);
      if (index !== -1) {
        this.users[index] = this.selectedUser;
      }
      this.isEditMode = false;
      this.selectedUser = null;
    }
  }
}
