import { Component } from '@angular/core';
import { HttpClient } from "@angular/common/http";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
interface User {
  userID: string;
  username: string;
  password: string;
}
export class AppComponent {

  users: any[] = [];
  currentUser: any = {};
  isUpdateMode = false;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    // Fetch initial user data from your server
    this.http.get<any[]>('http://localhost:3000/users').subscribe(data => {
      this.users = data;
    });
  }

  addOrUpdateUser() {
    if (this.isUpdateMode) {
      // Update user
      this.http.put(`http://localhost:3000/users/${this.currentUser.userID}`, this.currentUser)
        .subscribe(updatedUser => {
          const index = this.users.findIndex(user => user.userID === updatedUser.userID);
          if (index !== -1) {
            this.users[index] = updatedUser;
          }
        });
    } else {
      // Add user
      this.http.post('http://localhost:3000/users', this.currentUser)
        .subscribe(newUser => {
          this.users.push(newUser);
        });
    }
    this.currentUser = {};
    this.isUpdateMode = false;
  }

  editUser(user: any) {
    this.currentUser = { ...user };
    this.isUpdateMode = true;
  }

  deleteUser(user: any) {
    // Delete user
    this.http.delete(`http://localhost:3000/users/${user.userID}`).subscribe(() => {
      const index = this.users.indexOf(user);
      if (index !== -1) {
        this.users.splice(index, 1);
      }
    });
  }

  updateUser() {
    // Update user
    this.http.put(`http://localhost:3000/users/${this.currentUser.userID}`, this.currentUser)
      .subscribe(updatedUser => {
        const index = this.users.findIndex(user => user.userID === updatedUser.userID);
        if (index !== -1) {
          this.users[index] = updatedUser;
        }
      });
    this.currentUser = {};
    this.isUpdateMode = false;
  }
}
