import { Component } from '@angular/core';
import { HttpClient } from "@angular/common/http";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'AngularExpress';
  userList: any = [];
  expresponse: string = "";
  newUser: any = { userId: '', password: '', emailId: '' }; // Model for user data

  constructor(private http: HttpClient) {
    this.getAllUsers();
  }

  getAllUsers() {
    this.http.get('http://localhost:8000/getAll').subscribe((response) => {
      this.userList = response;
    });
  }

  addUser() {
    console.log(this.newUser);
    this.http.post('http://localhost:8000/insert', this.newUser).subscribe((response) => {
     this.expresponse = response.toString();
    this.getAllUsers(); // Refresh the user list after adding a user
    //   this.newUser = { userId: '', password: '', emailId: '' }; // Clear the form
    // });
  }
}
<form (ngSubmit)="addUser()">
  User Id <input type="text" name="userId" [(ngModel)]="newUser.userId" /><br>
  Password <input type="text" name="password" [(ngModel)]="newUser.password" /><br>
  Email Id <input type="email" name="emailId" [(ngModel)]="newUser.emailId" /><br>
  <input type="submit" value="Add User" />&nbsp;&nbsp;
  <input type="button" value="Get User" (click)="getAllUsers()" />
</form>

<!-- ... -->

<table border="1">
  <tr>
    <th>User ID</th>
    <th>Password</th>
    <th>Email ID</th>
    <th>Actions</th>
  </tr>
  <tr *ngFor="let user of userList">
    <td>{{ user.userId }}</td>
    <td>{{ user.password }}</td>
    <td>{{ user.emailId }}</td>
    <td>
      <button >Delete</button>
    </td>
  </tr>
</table>
