import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class BackendAccessService {
  userList: any[] = [];
  expresponse: string = "";

  constructor(private http: HttpClient) {
  }

  getAllUsers(): Observable<any> {
    // You should create an Express or another backend server that connects to MongoDB.
    // Replace the URL with your backend API endpoint.
    return this.http.get('http://your-backend-api/getAll');
  }

  addUser(udata: any): Observable<any> {
    // You should create an Express or another backend server that connects to MongoDB.
    // Replace the URL with your backend API endpoint.
    return this.http.post('http://your-backend-api/insert', udata);
  }
}
